# Initialize variables.
p_turn_k = 0.4
p_turn_c = 22
target_heading = 0
previous_heading = [0]
prev_heading_num = 0

def p_turn__pivot_(desired):
    pass

def p_turn__heading_(desired, left_motor, right_motor, prime_hub):
    global prev_heading_num
    if previous_heading[prev_heading_num] >= desired:
        while prime_hub.imu.heading() > desired:
            left_motor.dc(power_function(p_turn_k, -1 * p_turn_c, error(prime_hub.imu.heading(), desired), 1))
            right_motor.dc(power_function(p_turn_k, -1 * p_turn_c, error(prime_hub.imu.heading(), desired), -1))
            print(prime_hub.imu.heading())
    else:
        while prime_hub.imu.heading() < desired:
            left_motor.dc(power_function(p_turn_k, p_turn_c, error(prime_hub.imu.heading(), desired), 1))
            right_motor.dc(power_function(p_turn_k, p_turn_c, error(prime_hub.imu.heading(), desired), -1))
            print(prime_hub.imu.heading())
    previous_heading.append(desired)
    prev_heading_num = prev_heading_num + 1

def power_function(k, c, err, a):
    return a * (k * err + c)

def error(current, desired):
    return desired - current

def p_turn__incremental_(turn_amount, left_motor, right_motor, prime_hub):
    global target_heading
    target_heading = previous_heading[prev_heading_num] + turn_amount
    p_turn__heading_(target_heading, left_motor, right_motor, prime_hub)

def p_turn__pivot_incremental_(turn_amount):
    global target_heading
    target_heading = previous_heading[prev_heading_num] + turn_amount
    p_turn__pivot_(target_heading)


