from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

from p_turn import p_turn_incremental_sync

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left_light_sensor = ColorSensor(Port.B)
Right_light_sensor = ColorSensor(Port.A)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 110)
left_attachment_motor = Motor(Port.D, Direction.CLOCKWISE)
right_attachment_motor = Motor(Port.C, Direction.CLOCKWISE)

def p_turn_increment(angle):
    p_turn_incremental_sync(angle, left, right, prime_hub)


# The main program starts here.
drive_base.use_gyro(True)
drive_base.settings(straight_speed=250)
drive_base.straight(-365)
wait(25)
drive_base.settings(straight_speed=250)
drive_base.straight(160)
p_turn_increment(-49.5)
drive_base.straight(-196)
p_turn_increment(49.5)
drive_base.straight(-130)
drive_base.straight(100)
p_turn_increment(-15)
while not Right_light_sensor.reflection() < 20:
    drive_base.drive(-250, 0)
p_turn_increment(15)
wait(25)
p_turn_increment(38.5)
right_attachment_motor.reset_angle(0)
right_attachment_motor.run_angle(950, 287.5)
drive_base.straight(175)
p_turn_increment(15)
right_attachment_motor.run_angle(360, -292.5)
drive_base.straight(-150)
p_turn_increment(-45)
drive_base.settings(straight_speed=1000)
drive_base.straight(-900)
